    );
  };

  let totalAmount = 0;
  const shoppingCart = cartState;
  const itemsinShoppingCart = shoppingCart.map(addItem);

  shoppingCart.forEach(item => {
    sum = +(item.price) + sum
  });

  return <div>
    <list>{itemsinShoppingCart}</list>
    <p><b>Total Amount: </b>{totalAmount}</p>
  </div>;
}
