import 'bootstrap/dist/css/bootstrap.min.css';
import "./App.css";
import { useState } from "react";
import bakeryData from "./assets/bakery-data.json";
import BakeryItem from "./components/BakeryItem";
import Cart from "./components/Cart";

/* ####### DO NOT TOUCH -- this makes the image URLs work ####### */
bakeryData.forEach((item) => {
  item.image = process.env.PUBLIC_URL + "/" + item.image;
});
/* ############################################################## */

function App() {
  // TODO: use useState to create a state variable to hold the state of the cart
  /* add your cart state code here */

  const [cartState, setCartState] = useState([]);

  const clickItem = (item) => {
    setCartState([...cartState, { name: item.name, price: item.price, key: Date.now() }]);
  };

  return (
    <div class="flex-col" className="App">
      <div class='d-flex row'>
        <div class="flex col-sm-6">
          <h1>My Bakery</h1>
          <div class='row row-cols-md-2'>
          {bakeryData.map((item) => { // TODO: map bakeryData to BakeryItem components
  return (<BakeryItem name={item.name} image={item.image} price={item.price} description={item.description} clickItem={clickItem} />);
})}
          </div>
        </div>
        <aside class='col-sm-4'>
          <h2>Cart</h2>
          <Cart cartState={cartState}/>
        </aside>
      </div>
    </div>
  );
}

export default App;
