// TODO: create a component that displays a single bakery item
import App from '../App'

const BakeryItem = ({item}) => {
    return (
        <div class="BakeryItem">
            <div class="BakeryImage">
                <img src={item.image}/>
                <h2>{item.name}</h2>
                <p>{item.description}</p>
                <p>{item.price}</p>
            </div>
            <button onClick={() => item.handleonClick(item)}>Add to Cart</button>
        </div>
    )
};

export default BakeryItem;
